function pre($array)
{
	echo PHP_EOL;
	echo '<pre style="background: #FFFEEF; color: #333; border: 1px dashed #CCC; padding: 1.5rem; margin: 1rem 0; text-align: left;">';
	echo PHP_EOL;
    print_r($array);
	echo '</pre>';
	echo PHP_EOL;
}

function get_domain($url)
{
	$pieces = parse_url($url);
	$domain = isset($pieces['host']) ? $pieces['host'] : $pieces['path'];
	if (preg_match('/(?P<domain>[a-z0-9][a-z0-9\-]{1,63}\.[a-z\.]{2,6})$/i', $domain, $regs)) {
	return $regs['domain'];
	}
	return false;
}

function pre_exit($array)
{
    pre($array);
    exit;
}
