function cleanMdHTML($content){
	$content 	= cleanHtml($content);
	$removeChar = array('https:', 'http:');
	$content 	= str_replace($removeChar, '', $content);
	$removeChar = array('–', ':');
	$content 	= str_replace($removeChar, '-', $content);
	return $content;
}

function cleanHtml($content){
	$CI = get_instance();
	$CI->load->helper('text');
	$content	= convert_accented_characters($content);
	$patterns	= array(
		'/\s+/',
		'/(<[^>]+) style=".*?"/i',
		'/(<[^>]+) class=".*?"/i' 
	);
	$replacements = array(
		' ',
		'$1',
		'$1' 
	);
	$content 	= str_replace("’", "'", $content);
	$removeChar = array('<p></p>');
	$content 	= str_replace($removeChar, '', $content);
	$removeChar = array('<p>', '</p>');
	$content 	= str_replace($removeChar, array('', '<br>'), $content);
	$content 	= preg_replace($patterns, $replacements, $content);
	$content 	= str_replace("&amp;nbsp;", " ", $content);
	$content = preg_replace('/[[:^print:]]/', '', $content);
	$content 	= str_replace("&nbsp;", " ", $content);
	$content 	= strip_tags($content,'<b><strong><small><i><a><br><ul><ol><li><table></table><tr></tr><td></td>');
	$removeChar = array('&nbsp;','&quot;','<p></p>', '<strong><br></strong>');
	$content 	= str_replace($removeChar, '', $content);
	$content = preg_replace('/<br\\s*?\\/?>\\s*$/', '', $content);
	$content 	= remove_emoji($content);
	return $content;
}
function cleanMdHTML_i18n($content){
	$content 	= cleanHtml_i18n($content);
	$removeChar = array('https:', 'http:');
	$content 	= str_replace($removeChar, '', $content);
	$removeChar = array('–', ':');
	$content 	= str_replace($removeChar, '-', $content);
	return $content;
}
function cleanHtml_i18n($content){
	$CI = get_instance();
	$CI->load->helper('text');
	$content	= convert_accented_characters($content);
	$patterns	= array(
		'/\s+/',
		'/(<[^>]+) style=".*?"/i',
		'/(<[^>]+) class=".*?"/i' 
	);
	$replacements = array(
		' ',
		'$1',
		'$1' 
	);
	$content 	= ltrim($content);
	$content 	= ltrim($content,",");
	//$content 	= str_replace("’", "'", $content);
	$removeChar = array('<p></p>');
	$content 	= str_replace($removeChar, '', $content);
	$removeChar = array('<p>', '</p>');
	$content 	= str_replace($removeChar, array('', '<br>'), $content);
	$content 	= preg_replace($patterns, $replacements, $content);
	$content 	= str_replace("&amp;nbsp;", " ", $content);
	//$content = preg_replace('/[[:^print:]]/', '', $content);
	$content 	= str_replace("&nbsp;", " ", $content);
	$content 	= strip_tags($content,'<b><strong><small><i><a><br><ul><ol><li><table></table><tr></tr><td></td>');
	$removeChar = array('&nbsp;','&quot;','<p></p>', '<strong><br></strong>');
	$content 	= str_replace($removeChar, '', $content);
	$content = preg_replace('/<br\\s*?\\/?>\\s*$/', '', $content);
	$content 	= remove_emoji($content);
	return $content;
}

