function get_week_dates($the_date = NULL, $format= 'Y-m-d', $if_same_day_use_last_week = FALSE,$day_start = 'sunday')
    {
    $week = array('start'=>NULL,'end' => NULL);
    $date = new DateTime($the_date);
    
    $starting_day = ($day_start == 'any' ? $date->format('l'):$day_start);
    
    if($date->format('l') == ucfirst($starting_day)) 
    { 
        if(!$if_same_day_use_last_week)
        {
            $date->add(new DateInterval('P3D'));
        } 
        else {
            $date->sub(new DateInterval('P3D'));
        }
    
    }
    $week['start'] = date($format, strtotime('LAST '. strtoupper($starting_day) ,$date->getTimestamp()));
    
    if($date->format('l') <> ucfirst($starting_day))
    {
        $week['end'] = date($format, strtotime('NEXT '.strtoupper($starting_day).' -1 day',$date->getTimestamp()));
    }
    else{
        $week['end'] = date($format, $date->getTimestamp());
    }

    return $week;
} 